using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define the column widths\n        int columnWidth = 15;\n        \n        // Print the table header\n        PrintLine(columnWidth);\n        PrintRow(columnWidth, "Column 1", "Column 2", "Column 3", "Column 4");\n        PrintLine(columnWidth);\n\n        while (true)\n        {\n            // Generate or fetch dynamic data\n            string col1Data = "Data1";\n            string col2Data = "Data2";\n            string col3Data = "Data3";\n            string col4Data = "Data4";\n\n            // Print the data rows\n            PrintRow(columnWidth, col1Data, col2Data, col3Data, col4Data);\n            PrintLine(columnWidth);\n\n            // Sleep for a short period to simulate fast-changing data\n            Thread.Sleep(500);\n\n            // Clear the console to update the table\n            Console.Clear();\n        }\n    }\n\n    static void PrintLine(int columnWidth)\n    {\n        string line = new string('-', columnWidth * 4 + 5);\n        Console.WriteLine(line);\n    }\n\n    static void PrintRow(int columnWidth, params string[] columns)\n    {\n        string row = "|";\n\n        foreach (string column in columns)\n        {\n            row += AlignCenter(column, columnWidth) + "|";\n        }\n\n        Console.WriteLine(row);\n    }\n\n    static string AlignCenter(string text, int width)\n    {\n        if (text.Length > width)\n        {\n            text = text.Substring(0, width - 3) + "...";\n        }\n\n        int padding = width - text.Length;\n        int padLeft = padding / 2 + text.Length;\n        return text.PadLeft(padLeft).PadRight(width);\n    }\n}