using System;\nusing System.Net;\nusing System.Net.Mail;\nusing System.Windows.Forms;\n\npublic class EmailSender\n{\n    public static void SendEmail(string toAddress, string subject, RichTextBox richTextBox)\n    {\n        string htmlBody = ConvertRtfToHtml(richTextBox.Rtf);\n\n        MailMessage mail = new MailMessage();\n        mail.From = new MailAddress("youremail@example.com");\n        mail.To.Add(toAddress);\n        mail.Subject = subject;\n        mail.Body = htmlBody;\n        mail.IsBodyHtml = true;\n\n        SmtpClient smtpClient = new SmtpClient("smtp.example.com");\n        smtpClient.Port = 587; // or your SMTP port\n        smtpClient.Credentials = new NetworkCredential("yourusername", "yourpassword");\n        smtpClient.EnableSsl = true;\n\n        try\n        {\n            smtpClient.Send(mail);\n            MessageBox.Show("Email sent successfully!");\n        }\n        catch (Exception ex)\n        {\n            MessageBox.Show("Failed to send email: " + ex.Message);\n        }\n    }\n\n    private static string ConvertRtfToHtml(string rtf)\n    {\n        using (RichTextBox rtb = new RichTextBox())\n        {\n            rtb.Rtf = rtf;\n            return rtb.Text; // this is a simple conversion, you might need a more robust conversion\n        }\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static void Main()\n    {\n        RichTextBox richTextBox = new RichTextBox();\n        richTextBox.Rtf = @"{\rtf1\ansi This is \b bold\b0  and this is \i italic\i0 .}";\n\n        EmailSender.SendEmail("recipient@example.com", "Test Subject", richTextBox);\n    }\n}