public bool SomeMethod(string FileName)\n{\n    if (string.IsNullOrEmpty(TempPath))\n    {\n        throw new ArgumentNullException(nameof(TempPath), "TempPath cannot be null or empty.");\n    }\n\n    if (string.IsNullOrEmpty(FileName))\n    {\n        throw new ArgumentNullException(nameof(FileName), "FileName cannot be null or empty.");\n    }\n\n    string FullPath = TempPath + FileName;\n\n    System.Drawing.Image Adimg = null;\n\n    try\n    {\n        Adimg = System.Drawing.Image.FromFile(MapPath(FullPath));\n    }\n    catch (Exception ex)\n    {\n        // Handle exception (e.g., log the error)\n        throw new Exception("Error loading image from file.", ex);\n    }\n\n    // Further processing with Adimg...\n    return true;\n}\n\n// Example usage of MapPath method\nprivate string MapPath(string virtualPath)\n{\n    return HttpContext.Current.Server.MapPath(virtualPath);\n}