using System.IO;\nusing System.Web.UI;\n\npublic partial class YourPage : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Add event handler for the CheckBox\n        chk_CheckAll.CheckedChanged += new EventHandler(chk_CheckAll_CheckedChanged);\n    }\n\n    private void chk_CheckAll_CheckedChanged(object sender, EventArgs e)\n    {\n        // Trigger a re-render to modify the HTML output\n        Page.Response.Redirect(Page.Request.Url.ToString(), true);\n    }\n\n    protected override void Render(HtmlTextWriter writer)\n    {\n        // Capture the HTML output\n        StringWriter stringWriter = new StringWriter();\n        HtmlTextWriter htmlWriter = new HtmlTextWriter(stringWriter);\n        base.Render(htmlWriter);\n        htmlWriter.Flush();\n\n        // Get the generated HTML\n        string htmlOutput = stringWriter.ToString();\n\n        // Modify the HTML output\n        if (chk_CheckAll.Checked)\n        {\n            htmlOutput = htmlOutput.Replace("<input type=\"checkbox\" />", "<input type=\"checkbox\" disabled checked />");\n        }\n\n        // Write the modified HTML back to the response\n        writer.Write(htmlOutput);\n    }\n}