using System;\nusing System.Globalization;\n\npublic static class NumericChecker\n{\n    public static bool IsNumeric(string someValue)\n    {\n        if (string.IsNullOrWhiteSpace(someValue))\n        {\n            return false;\n        }\n\n        // Attempt to parse the string as a double\n        double number;\n        return double.TryParse(someValue, NumberStyles.Any, CultureInfo.InvariantCulture, out number);\n    }\n}