using System;\nusing System.Collections.Generic;\nusing System.DirectoryServices;\n\npublic class ActiveDirectoryHelper\n{\n    public List<string> GetAllGroups()\n    {\n        List<string> groups = new List<string>();\n\n        try\n        {\n            using (DirectoryEntry entry = new DirectoryEntry("LDAP://YourDomainController"))\n            {\n                using (DirectorySearcher searcher = new DirectorySearcher(entry))\n                {\n                    searcher.Filter = "(objectCategory=group)";\n                    searcher.PropertiesToLoad.Add("cn");\n\n                    foreach (SearchResult result in searcher.FindAll())\n                    {\n                        if (result.Properties["cn"].Count > 0)\n                        {\n                            groups.Add(result.Properties["cn"][0].ToString());\n                        }\n                    }\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            // Handle exceptions (log or rethrow as appropriate for your application)\n            Console.WriteLine("Error: " + ex.Message);\n        }\n\n        return groups;\n    }\n}