using HtmlAgilityPack;\nusing System;\n\n// Create an instance of HtmlDocument and load your HTML content\nHtmlDocument doc = new HtmlDocument();\ndoc.LoadHtml(htmlContent); // Assuming htmlContent is your HTML string\n\n// Use XPath to select the img node\nvar imgNode = doc.DocumentNode.SelectSingleNode("//div[@id='topslot']/a/img");\n\n// Check if the img node is found and then get the 'src' attribute value\nif (imgNode != null)\n{\n    var srcAttribute = imgNode.GetAttributeValue("src", string.Empty);\n    Console.WriteLine(srcAttribute);\n}\nelse\n{\n    Console.WriteLine("Image node not found.");\n}