using System;\nusing System.Diagnostics;\nusing IBM.Data.Informix;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "..."; // Your connection string here\n        using (IfxConnection connection = new IfxConnection(connectionString))\n        {\n            connection.Open();\n            string query = "SELECT id, data FROM aaa";\n            using (IfxCommand command = new IfxCommand(query, connection))\n            {\n                using (IfxDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        // Check if the data column is not null\n                        if (!reader.IsDBNull(1))\n                        {\n                            IfxBlob blob = reader.GetIfxBlob(1);\n                            Debug.WriteLine(blob.GetType());\n                        }\n                        else\n                        {\n                            Debug.WriteLine("System.DBNull");\n                        }\n                    }\n                }\n            }\n        }\n    }\n}