using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\nclass Program\n{\n    static void Main()\n    {\n        List<string> pdfFiles = new List<string>\n        {\n            @"c:\reports\WeeklyReport1.pdf",\n            @"c:\reports\WeeklyReport2.pdf",\n            @"c:\reports\WeeklyReport3.pdf",\n            // Add more report file paths here\n        };\n\n        string outputFilePath = @"c:\reports\CombinedWeeklyReports.pdf";\n        CombinePDFs(pdfFiles, outputFilePath);\n    }\n\n    public static void CombinePDFs(List<string> fileNames, string outFile)\n    {\n        using (FileStream stream = new FileStream(outFile, FileMode.Create))\n        {\n            Document document = new Document();\n            PdfCopy pdf = new PdfCopy(document, stream);\n            PdfReader reader = null;\n            try\n            {\n                document.Open();\n                foreach (string file in fileNames)\n                {\n                    reader = new PdfReader(file);\n                    pdf.AddDocument(reader);\n                    reader.Close();\n                }\n            }\n            catch (Exception ex)\n            {\n                // Handle the exception\n                Console.WriteLine("Exception: " + ex.Message);\n            }\n            finally\n            {\n                if (reader != null)\n                {\n                    reader.Close();\n                }\n                document.Close();\n            }\n        }\n    }\n}