using System;\nusing Markdig;\nusing Markdig.Renderers.Html;\n\nclass Program\n{\n    static void Main()\n    {\n        string markdown = @"\n<p>it seems that Markdown.NET does not escape the following:</p>\n\n<pre><code><script>\n</code></pre>\n\n<p>which is kind of a problem...</p>";\n\n        // Configure Markdig pipeline with HTML escaping\n        var pipeline = new MarkdownPipelineBuilder()\n            .UseAdvancedExtensions()\n            .UsePreciseSourceLocation()\n            .Build();\n\n        // Convert Markdown to HTML\n        string html = Markdown.ToHtml(markdown, pipeline);\n\n        // Output the HTML\n        Console.WriteLine(html);\n    }\n}