protected void Page_Load(object sender, EventArgs e)\n   {\n       if (!IsPostBack)\n       {\n           // Only check for the hidden field value on initial page load\n           if (hfShowModal.Value == "true")\n           {\n               ClientScript.RegisterStartupScript(Page.GetType(), "ModalPopup", "Sys.Application.add_load(function() { ShowModalPopup('" + formModalButton.ID + "'); });", true);\n           }\n       }\n   }