public static void Function(int i, string s)\n{\n    if (i <= 0)\n    {\n        throw new ArgumentOutOfRangeException(nameof(i), "The argument must be greater than zero.");\n    }\n\n    if (s == null)\n    {\n        throw new ArgumentNullException(nameof(s), "The argument cannot be null.");\n    }\n\n    if (s.Length == 0)\n    {\n        throw new ArgumentException("The argument cannot be an empty string.", nameof(s));\n    }\n\n    // Function logic goes here\n}