using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\nusing log4net;\n\npublic class OracleHelper\n{\n    private static readonly ILog log = LogManager.GetLogger(typeof(OracleHelper));\n\n    public static void ExecuteParameterizedQuery(string connectionString, string statement, params OracleParameter[] parameters)\n    {\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        using (OracleCommand command = new OracleCommand(statement, connection))\n        {\n            command.Parameters.AddRange(parameters);\n            connection.Open();\n\n            // Log the SQL statement with parameters replaced\n            string readableQuery = GetReadableQuery(command);\n            log.DebugFormat("Executing statement: {0}", readableQuery);\n\n            command.ExecuteNonQuery();\n            connection.Close();\n        }\n    }\n\n    private static string GetReadableQuery(OracleCommand command)\n    {\n        string query = command.CommandText;\n        foreach (OracleParameter param in command.Parameters)\n        {\n            string paramValue = param.Value == null ? "NULL" : param.Value.ToString();\n            if (param.DbType == DbType.String || param.DbType == DbType.Date)\n            {\n                paramValue = $"'{paramValue}'";\n            }\n            query = query.Replace(param.ParameterName, paramValue);\n        }\n        return query;\n    }\n}