using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing Microsoft.SqlServer.Server;\n\npublic class Triggers\n{\n    [SqlTrigger(Name = "TriggerOnInsert", Target = "YourTable", Event = "FOR INSERT")]\n    public static void TriggerOnInsert()\n    {\n        SqlTriggerContext triggerContext = SqlContext.TriggerContext;\n\n        if (triggerContext.TriggerAction == TriggerAction.Insert)\n        {\n            using (SqlConnection connection = new SqlConnection("context connection=true"))\n            {\n                connection.Open();\n                SqlCommand command = new SqlCommand(@"SELECT * FROM INSERTED;", connection);\n                SqlDataReader reader = command.ExecuteReader();\n\n                while (reader.Read())\n                {\n                    // Extract data from INSERTED pseudo-table\n                    var newData = reader["YourColumnName"];\n\n                    // Call external service or perform business logic\n                    CallExternalService(newData);\n                }\n            }\n        }\n    }\n\n    private static void CallExternalService(object newData)\n    {\n        // Code to call your external service\n    }\n}