public class AzManAuthorizationProvider\n{\n    public bool Authorize(string username, string operation)\n    {\n        // Implement your AzMan logic here\n        // Example:\n        using (AzAuthorizationStoreClass store = new AzAuthorizationStoreClass())\n        {\n            store.Initialize(0, "msxml://path_to_store.xml", null);\n            IAzApplication app = store.OpenApplication("YourAppName", null);\n            IAzOperation op = app.OpenOperation(operation, null);\n            IAzClientContext clientContext = app.InitializeClientContextFromName(username, null);\n            return clientContext.AccessCheck("AccessCheck", null, op.Name, null, null, null, null, null) == 0;\n        }\n    }\n}\n\n// Usage with a custom attribute\npublic class AzManAuthorizeAttribute : AuthorizeAttribute\n{\n    private readonly string _operation;\n\n    public AzManAuthorizeAttribute(string operation)\n    {\n        _operation = operation;\n    }\n\n    protected override bool AuthorizeCore(HttpContextBase httpContext)\n    {\n        var username = httpContext.User.Identity.Name;\n        var provider = new AzManAuthorizationProvider();\n        return provider.Authorize(username, _operation);\n    }\n}