using System;\nusing System.Globalization;\n\npublic class CurrencyFormatter\n{\n    public static string FormatCurrencyWithCode(decimal amount, string cultureName)\n    {\n        CultureInfo cultureInfo = new CultureInfo(cultureName);\n        NumberFormatInfo numberFormatInfo = (NumberFormatInfo)cultureInfo.NumberFormat.Clone();\n\n        // Use the currency symbol as a prefix with the currency code\n        string currencyCode = new RegionInfo(cultureInfo.Name).ISOCurrencySymbol;\n        numberFormatInfo.CurrencySymbol = currencyCode + " ";\n\n        return string.Format(cultureInfo, "{0:C}", amount);\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static void Main()\n    {\n        decimal price = 123.12m;\n\n        Console.WriteLine(CurrencyFormatter.FormatCurrencyWithCode(price, "en-US")); // Output: USD 123.12\n        Console.WriteLine(CurrencyFormatter.FormatCurrencyWithCode(price, "en-AU")); // Output: AUD 123.12\n        Console.WriteLine(CurrencyFormatter.FormatCurrencyWithCode(price, "en-GB")); // Output: GBP 123.12\n    }\n}