using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("user32.dll")]\n    public static extern int GetSystemMetrics(int nIndex);\n\n    static void Main()\n    {\n        // Example: Get the screen width\n        int screenWidth = GetSystemMetrics(0);\n        Console.WriteLine("Screen Width: " + screenWidth);\n\n        // Example: Check if the session is remote\n        int isRemoteSession = GetSystemMetrics(0x1000); // 0x1000 is the index for SM_REMOTESESSION\n        Console.WriteLine("Is Remote Session: " + (isRemoteSession != 0));\n    }\n}