using System;\n   using System.Data;\n   using Oracle.ManagedDataAccess.Client;\n\n   class Program\n   {\n       static void Main()\n       {\n           string oracleConnectionString = "User Id=<username>;Password=<password>;Data Source=<datasource>";\n\n           using (OracleConnection connection = new OracleConnection(oracleConnectionString))\n           {\n               connection.Open();\n\n               using (OracleBulkCopy bulkCopy = new OracleBulkCopy(connection))\n               {\n                   bulkCopy.DestinationTableName = "YourDestinationTable";\n\n                   // Create a DataTable with the same schema as the destination table\n                   DataTable dataTable = new DataTable();\n                   dataTable.Columns.Add("Column1", typeof(int));\n                   dataTable.Columns.Add("Column2", typeof(string));\n                   // Add more columns as needed\n\n                   // Fill the DataTable with data\n                   for (int i = 0; i < 1000; i++) // Example loop to add rows\n                   {\n                       dataTable.Rows.Add(i, "Value" + i);\n                   }\n\n                   // Perform bulk copy\n                   bulkCopy.WriteToServer(dataTable);\n               }\n           }\n       }\n   }