using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class Example\n{\n    public static void Main()\n    {\n        Dictionary<Guid, int> m_AvailableDict = new Dictionary<Guid, int>\n        {\n            { Guid.NewGuid(), 1 },\n            { Guid.NewGuid(), 0 },\n            { Guid.NewGuid(), 2 }\n        };\n\n        var available = m_AvailableDict.FirstOrDefault(p => p.Value == 0);\n\n        if (!EqualityComparer<KeyValuePair<Guid, int>>.Default.Equals(available, default(KeyValuePair<Guid, int>)))\n        {\n            Console.WriteLine("KeyValuePair found: Key = " + available.Key + ", Value = " + available.Value);\n        }\n        else\n        {\n            Console.WriteLine("No KeyValuePair found with the specified condition.");\n        }\n    }\n}