using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing Ionic.Zip;\n\nclass Program\n{\n    static void Main()\n    {\n        string fileToCompress = "path/to/your/file.txt";\n        string zipFileToCreate = "path/to/your/archive.zip";\n        string password = "YourStrongPassword";\n\n        // Create a zip file and add a file to it\n        using (ZipFile zip = new ZipFile())\n        {\n            zip.Password = password; // Set the password for encryption\n            zip.AddFile(fileToCompress, ""); // Add the file to the zip\n            zip.Save(zipFileToCreate); // Save the zip file\n        }\n\n        Console.WriteLine("File compressed and encrypted successfully.");\n    }\n}