using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\npublic class OracleTimestampExample\n{\n    public static void Main()\n    {\n        string connectionString = "User Id=<username>;Password=<password>;Data Source=<datasource>";\n        string queryString = "SELECT timestamp_column FROM your_table WHERE condition";\n\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            OracleCommand command = new OracleCommand(queryString, connection);\n            connection.Open();\n\n            using (OracleDataReader reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    OracleTimeStamp oracleTimestamp = reader.GetOracleTimeStamp(0);\n                    DateTime dateTime = oracleTimestamp.Value;\n\n                    Console.WriteLine("Oracle TIMESTAMP: " + oracleTimestamp.ToString());\n                    Console.WriteLine("Converted to .NET DateTime: " + dateTime.ToString("yyyy-MM-dd HH:mm:ss.fff"));\n                }\n            }\n        }\n    }\n}