using System;\nusing System.Data.SqlClient;\n\npublic class StoredProcChecker\n{\n    public static void Main()\n    {\n        string connectionString = "YourConnectionStringHere";\n        string[] storedProcedures = { "StoredProc1", "StoredProc2" }; // Add your stored procedures here\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            foreach (var proc in storedProcedures)\n            {\n                try\n                {\n                    string query = $@"\n                        BEGIN TRY\n                            DECLARE @sql NVARCHAR(MAX) = (SELECT definition FROM sys.sql_modules WHERE object_id = OBJECT_ID('{proc}'));\n                            EXEC sp_executesql @sql;\n                        END TRY\n                        BEGIN CATCH\n                            THROW;\n                        END CATCH";\n                    using (SqlCommand command = new SqlCommand(query, connection))\n                    {\n                        command.ExecuteNonQuery();\n                        Console.WriteLine($"{proc} compiled successfully.");\n                    }\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine($"Error compiling {proc}: {ex.Message}");\n                }\n            }\n        }\n    }\n}