using System;\nusing System.Data;\nusing System.Data.OleDb;\n\npublic class ExcelReader\n{\n    public DataSet ReadExcelFile(string filePath)\n    {\n        string connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=" + filePath + ";Extended Properties=\"Excel 8.0;HDR=YES;\"";\n        \n        using (OleDbConnection connection = new OleDbConnection(connectionString))\n        {\n            try\n            {\n                connection.Open();\n\n                string query = "SELECT * FROM [Sheet1$]";\n                OleDbDataAdapter adapter = new OleDbDataAdapter(query, connection);\n                DataSet dataSet = new DataSet();\n                \n                adapter.Fill(dataSet);\n                \n                return dataSet;\n            }\n            catch (Exception ex)\n            {\n                // Log the exception details here\n                Console.WriteLine($"Error: {ex.Message}");\n                // Optionally, throw the exception to be handled by the calling code\n                throw;\n            }\n        }\n    }\n}