<%@ WebHandler Language="C#" Class="RedirectHandler" %>\n\nusing System;\nusing System.Web;\n\npublic class RedirectHandler : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        string host = context.Request.Url.Host.ToLower();\n\n        if (host == "mydomain1.com")\n        {\n            context.Response.Redirect("http://longurl1.com");\n        }\n        else if (host == "mydomain2.com")\n        {\n            context.Response.Redirect("http://longurl2.com");\n        }\n        else\n        {\n            context.Response.StatusCode = 404;\n            context.Response.StatusDescription = "Not Found";\n            context.Response.End();\n        }\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}