using System;\nusing System.Web;\nusing System.Web.SessionState;\n\npublic class HeartBeatHandler : IHttpHandler, IRequiresSessionState\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        if (context.Session != null)\n        {\n            // Update the session timestamp to indicate activity\n            context.Session["LastHeartbeat"] = DateTime.Now;\n            context.Response.StatusCode = 200;\n            context.Response.Write("Heartbeat received");\n        }\n        else\n        {\n            context.Response.StatusCode = 500;\n            context.Response.Write("Session not available");\n        }\n    }\n\n    public bool IsReusable\n    {\n        get { return false; }\n    }\n}