using System;\nusing System.Collections.Generic;\nusing System.IO;\nusing System.Linq;\nusing Microsoft.AspNetCore.Mvc;\nusing Microsoft.AspNetCore.Mvc.Rendering;\nusing CsvHelper;\n\npublic class Airport\n{\n    public string IATA { get; set; }\n    public string Name { get; set; }\n    public string City { get; set; }\n    public string Country { get; set; }\n}\n\npublic class AirportsController : Controller\n{\n    public IActionResult Index()\n    {\n        var airports = LoadAirportsFromCsv("path/to/airports.csv");\n        var airportSelectList = airports.Select(a => new SelectListItem\n        {\n            Value = a.IATA,\n            Text = $"{a.City}, {a.Country} ({a.IATA})"\n        }).ToList();\n        \n        ViewBag.Airports = airportSelectList;\n        return View();\n    }\n\n    private List<Airport> LoadAirportsFromCsv(string filePath)\n    {\n        using (var reader = new StreamReader(filePath))\n        using (var csv = new CsvReader(reader))\n        {\n            return csv.GetRecords<Airport>().ToList();\n        }\n    }\n}