class Program\n{\n    static void Main(string[] args)\n    {\n        XInput.XINPUT_STATE state;\n        if (XInput.GetInputState(0, out state))\n        {\n            Console.WriteLine("Controller Connected");\n            Console.WriteLine("Buttons: " + state.Gamepad.wButtons);\n            Console.WriteLine("Left Trigger: " + state.Gamepad.bLeftTrigger);\n            Console.WriteLine("Right Trigger: " + state.Gamepad.bRightTrigger);\n            Console.WriteLine("Left Thumbstick: (" + state.Gamepad.sThumbLX + ", " + state.Gamepad.sThumbLY + ")");\n            Console.WriteLine("Right Thumbstick: (" + state.Gamepad.sThumbRX + ", " + state.Gamepad.sThumbRY + ")");\n        }\n        else\n        {\n            Console.WriteLine("Controller Not Connected");\n        }\n    }\n}