public string BuildHtml(bool hasLink, string link, bool hasCaption, string caption)\n{\n    var anchorStartTag = hasLink ? $"<a href=\"{link}\">" : string.Empty;\n    var anchorEndTag = hasLink ? "</a>" : string.Empty;\n    var captionHtml = hasCaption ? $"<br>{caption}" : string.Empty;\n\n    return $@"\n<!DOCTYPE HTML PUBLIC ""-//W3C//DTD HTML 4.01//EN""\n""http://www.w3.org/TR/html4/strict.dtd"">\n<html>\n<head>\n    <title>New Document</title>\n</head>\n<body>\n    {anchorStartTag}\n    {anchorEndTag}\n    {captionHtml}\n</body>\n</html>";\n}\n\n// Example usage:\nvar html = BuildHtml(true, "http://example.com", true, "Example Caption");\nConsole.WriteLine(html);