var orderedRecords = yourDbContext.YourTable\n    .Select(record => new \n    {\n        DisplayName = record.IsCompany ? record.CompanyName : record.LastName + ", " + record.FirstName,\n        IsCompany = record.IsCompany,\n        LastName = record.LastName,\n        FirstName = record.FirstName,\n        CompanyName = record.CompanyName\n    })\n    .OrderBy(record => record.DisplayName)\n    .ToList();