// Save object to temporary storage and generate unique ID\n   string uniqueId = Guid.NewGuid().ToString();\n   TemporaryStorage.Save(uniqueId, myObject);\n\n   // Pass unique ID in QueryString\n   Response.Redirect($"NextPage.aspx?id={uniqueId}");\n\n   // Retrieve object from temporary storage on the next page\n   string uniqueId = Request.QueryString["id"];\n   var myObject = TemporaryStorage.Get(uniqueId);