using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        Process[] processList = Process.GetProcesses();\n\n        foreach (Process process in processList)\n        {\n            try\n            {\n                Console.WriteLine($"Process: {process.ProcessName} (ID: {process.Id})");\n                Console.WriteLine($"Memory Usage: {process.WorkingSet64 / 1024} KB");\n                Console.WriteLine($"CPU Time: {process.TotalProcessorTime}");\n                Console.WriteLine();\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Could not retrieve information for process {process.ProcessName}: {ex.Message}");\n            }\n        }\n    }\n}