using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlContent = @"<?xml version=""1.0""?>\n                              <itemSet>\n                                 <Item>one</Item>\n                                 <Item>two</Item>\n                                 <Item>three</Item>\n                              </itemSet>";\n\n        XmlDocument xmlDoc = new XmlDocument();\n        xmlDoc.LoadXml(xmlContent);\n\n        XmlNode node = xmlDoc.SelectSingleNode("/itemSet/Item[text()='two']");\n\n        if (node != null)\n        {\n            Console.WriteLine("Found node: " + node.OuterXml);\n        }\n        else\n        {\n            Console.WriteLine("Node not found.");\n        }\n    }\n}