using System;\nusing System.Reflection;\nusing System.Security;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string[] assemblies = { "assembly1.dll", "assembly2.dll", /* add other assemblies */ };\n        \n        foreach (var assemblyPath in assemblies)\n        {\n            try\n            {\n                Assembly assembly = Assembly.LoadFrom(assemblyPath);\n                var attributes = assembly.GetCustomAttributes(typeof(AllowPartiallyTrustedCallersAttribute), false);\n                \n                if (attributes.Length == 0)\n                {\n                    Console.WriteLine($"{assembly.GetName().Name} does NOT have AllowPartiallyTrustedCallers attribute.");\n                }\n                else\n                {\n                    Console.WriteLine($"{assembly.GetName().Name} has AllowPartiallyTrustedCallers attribute.");\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error loading assembly {assemblyPath}: {ex.Message}");\n            }\n        }\n    }\n}