using (var context = new YourDataContext())\n{\n    // Create a new entity object\n    var newEntity = new YourEntity { Property1 = "Value1", Property2 = "Value2" };\n    \n    // Add the new entity to the context\n    context.YourEntityTable.InsertOnSubmit(newEntity);\n    \n    // Access newly added entities from ChangeSet\n    var newEntities = context.GetChangeSet().Inserts.OfType<YourEntity>().Where(e => e.Property1 == "Value1");\n    \n    // Process the query results\n    foreach (var entity in newEntities)\n    {\n        Console.WriteLine(entity.Property1);\n    }\n    \n    // Submit changes to the database if necessary\n    context.SubmitChanges();\n}