using (var context = new YourDbContext())\n{\n    // Create a new entity object\n    var newEntity = new YourEntity { Property1 = "Value1", Property2 = "Value2" };\n    \n    // Add the new entity to the context\n    context.YourEntitySet.Add(newEntity);\n    \n    // Query the context to include the newly added entity\n    var query = context.YourEntitySet.Local.Where(e => e.Property1 == "Value1");\n    \n    // Process the query results\n    foreach (var entity in query)\n    {\n        Console.WriteLine(entity.Property1);\n    }\n    \n    // Save changes to the database if necessary\n    context.SaveChanges();\n}