using System;\nusing System.Diagnostics;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        Process currentProcess = Process.GetCurrentProcess();\n\n        while (true)\n        {\n            Console.WriteLine("Private Memory Size: " + currentProcess.PrivateMemorySize64 + " bytes");\n            Console.WriteLine("Virtual Memory Size: " + currentProcess.VirtualMemorySize64 + " bytes");\n            Console.WriteLine("Working Set: " + currentProcess.WorkingSet64 + " bytes");\n\n            Thread.Sleep(1000); // Poll every second\n        }\n    }\n}