try\n{\n    System.Diagnostics.Process p = new System.Diagnostics.Process();\n    p.StartInfo.FileName = CreatePDF(); // method that creates your PDF and returns the full path\n    p.StartInfo.UseShellExecute = true;\n    \n    if (!p.Start())\n    {\n        Controller.Error = "Opening the PDF file failed.";\n    }\n}\ncatch (Exception ex)\n{\n    Controller.Error = "Create PDF::" + ex.Message;\n}