using System;\n\npublic class FileSizeFormatter\n{\n    public static string GetHumanReadableFileSize(long fileSizeInBytes)\n    {\n        string[] sizeSuffixes = { "Bytes", "KB", "MB", "GB", "TB", "PB", "EB", "ZB", "YB" };\n        if (fileSizeInBytes == 0)\n        {\n            return "0 Bytes";\n        }\n\n        int magnitude = (int)Math.Log(fileSizeInBytes, 1024);\n        double adjustedSize = fileSizeInBytes / Math.Pow(1024, magnitude);\n\n        return string.Format("{0:0.##} {1}", adjustedSize, sizeSuffixes[magnitude]);\n    }\n\n    public static void Main(string[] args)\n    {\n        long fileSize = 7326629; // Example file size in bytes\n        string humanReadableSize = GetHumanReadableFileSize(fileSize);\n        Console.WriteLine(humanReadableSize); // Output: 6.98 MB\n    }\n}