using System;\n\npublic class TimeZoneHelper\n{\n    public static DateTime GetCurrentTimeInTimeZone(string timeZoneId)\n    {\n        // Get the time zone info for the specified ID\n        TimeZoneInfo targetTimeZone = TimeZoneInfo.FindSystemTimeZoneById(timeZoneId);\n        \n        // Get the current time in UTC\n        DateTime utcNow = DateTime.UtcNow;\n        \n        // Convert UTC time to the target time zone\n        DateTime targetTime = TimeZoneInfo.ConvertTimeFromUtc(utcNow, targetTimeZone);\n        \n        return targetTime;\n    }\n}\n\n// Example usage\nstring timeZoneId = "Eastern Standard Time"; // Replace with your target time zone ID\nDateTime currentTimeInTimeZoneB = TimeZoneHelper.GetCurrentTimeInTimeZone(timeZoneId);\nConsole.WriteLine("Current time in timezone B: " + currentTimeInTimeZoneB);