using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        string csvString = "value1,value2,value3,value4";\n        \n        // Convert CSV string to string array\n        string[] stringArray = csvString.Split(',');\n\n        // Convert CSV string to List<string>\n        List<string> stringList = new List<string>(stringArray);\n\n        // Output the results\n        Console.WriteLine("String Array:");\n        foreach (var item in stringArray)\n        {\n            Console.WriteLine(item);\n        }\n\n        Console.WriteLine("\nString List:");\n        foreach (var item in stringList)\n        {\n            Console.WriteLine(item);\n        }\n    }\n}