using System;\nusing System.Data.SqlClient;\nusing System.Text.RegularExpressions;\n\npublic class CreateViewExample\n{\n    public static void CreateView(string viewName, string viewBody)\n    {\n        // Basic validation to check for invalid characters\n        if (!Regex.IsMatch(viewName, @"^[a-zA-Z0-9_]+$"))\n        {\n            throw new ArgumentException("Invalid view name.");\n        }\n\n        // Additional validation for viewBody can be added here\n\n        string sql = $"CREATE VIEW {viewName} AS {viewBody}";\n        \n        using (SqlConnection conn = new SqlConnection("your_connection_string"))\n        {\n            SqlCommand cmd = new SqlCommand(sql, conn);\n            conn.Open();\n            cmd.ExecuteNonQuery();\n        }\n    }\n\n    public static void Main()\n    {\n        try\n        {\n            string viewName = "foo";\n            string viewBody = "SELECT * FROM bar";\n            CreateView(viewName, viewBody);\n            Console.WriteLine("View created successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}