using System.Web;\nusing System.Web.SessionState;\n\npublic class MyHttpModule : IHttpModule, IRequiresSessionState\n{\n    public void Init(HttpApplication context)\n    {\n        context.AcquireRequestState += OnAcquireRequestState;\n        context.EndRequest += OnEndRequest;\n    }\n\n    private void OnAcquireRequestState(object sender, EventArgs e)\n    {\n        HttpApplication app = (HttpApplication)sender;\n        HttpContext context = app.Context;\n\n        // Access session state here\n        if (context.Session != null)\n        {\n            context.Session["MyKey"] = "MyValue";\n        }\n    }\n\n    private void OnEndRequest(object sender, EventArgs e)\n    {\n        HttpApplication app = (HttpApplication)sender;\n        HttpContext context = app.Context;\n\n        // Ensure session state is saved\n        if (context.Session != null)\n        {\n            context.Session["MyKey"] = "MyUpdatedValue";\n        }\n    }\n\n    public void Dispose() { }\n}