using System;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class MainForm : Form\n{\n    private GroupBox groupBox;\n    private Label label;\n\n    public MainForm()\n    {\n        InitializeComponent();\n    }\n\n    private void InitializeComponent()\n    {\n        this.groupBox = new GroupBox();\n        this.label = new Label();\n        \n        // Set up GroupBox\n        this.groupBox.Text = "GroupBox Caption";\n        this.groupBox.ForeColor = SystemColors.ControlText;  // Use system-defined ControlText color\n        this.groupBox.Location = new Point(10, 10);\n        this.groupBox.Size = new Size(200, 100);\n        \n        // Set up Label\n        this.label.Text = "Label Text";\n        this.label.Location = new Point(20, 50);\n        this.label.AutoSize = true;\n        \n        // Synchronize label's ForeColor with GroupBox caption color\n        this.label.ForeColor = this.groupBox.ForeColor;\n        \n        // Add controls to the form\n        this.Controls.Add(this.groupBox);\n        this.groupBox.Controls.Add(this.label);\n        \n        // Set up the form\n        this.Text = "Main Form";\n        this.Size = new Size(300, 200);\n    }\n    \n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new MainForm());\n    }\n}