using System.Data.SqlServerCe;\n\nstring connectionString = "Data Source='myDatabase.sdf';";\n\nusing (SqlCeConnection connection = new SqlCeConnection(connectionString))\n{\n    connection.Open();\n\n    string insertQuery = "INSERT INTO Delivery (DateField, NumberField) VALUES (@DateField, @NumberField)";\n    using (SqlCeCommand command = new SqlCeCommand(insertQuery, connection))\n    {\n        command.Parameters.AddWithValue("@DateField", DateTime.Now);\n        command.Parameters.AddWithValue("@NumberField", 1234);\n\n        command.ExecuteNonQuery();\n    }\n}