public IEnumerable<MyType> GetData()\n{\n    var results = new List<MyType>();\n    \n    using (var connection = new SqlConnection("YourConnectionString"))\n    {\n        connection.Open();\n        using (var command = new SqlCommand("YourQuery", connection))\n        {\n            using (var reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    var item = new MyType\n                    {\n                        Property1 = reader["Column1"].ToString(),\n                        Property2 = Convert.ToInt32(reader["Column2"]),\n                        // Map other properties\n                    };\n                    results.Add(item);\n                }\n            }\n        }\n    }\n\n    return results;\n}