using System;\nusing System.Data.SqlClient;\n\ntry\n{\n    // Your database operations here\n}\ncatch (SqlException ex)\n{\n    switch (ex.Number)\n    {\n        case 2627: // Primary key violation\n            Console.WriteLine("Primary key violation. Cannot insert duplicate key.");\n            break;\n\n        case 547: // Foreign key violation\n            Console.WriteLine("Foreign key violation.");\n            break;\n\n        case 2601: // Unique index/constraint violation\n            Console.WriteLine("Unique index or constraint violation.");\n            break;\n\n        default:\n            Console.WriteLine($"SQL Error {ex.Number}: {ex.Message}");\n            break;\n    }\n}\ncatch (Exception ex)\n{\n    Console.WriteLine($"An error occurred: {ex.Message}");\n}