using System;\nusing System.Data.OracleClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "User Id=<username>;Password=<password>;Data Source=<datasource>";\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            connection.Open();\n\n            string[] scripts = new string[]\n            {\n                "CREATE OR REPLACE PROCEDURE Procedure1 IS BEGIN NULL; END;",\n                "CREATE OR REPLACE PROCEDURE Procedure2 IS BEGIN NULL; END;",\n                "CREATE OR REPLACE TRIGGER Trigger1 BEFORE INSERT ON MyTable FOR EACH ROW BEGIN NULL; END;"\n            };\n\n            foreach (var script in scripts)\n            {\n                using (OracleCommand command = new OracleCommand(script, connection))\n                {\n                    command.ExecuteNonQuery();\n                }\n            }\n        }\n    }\n}