using System;\nusing System.Windows.Forms;\n\npublic class TabControlDoubleClick : Form\n{\n    private TabControl tabControl;\n\n    public TabControlDoubleClick()\n    {\n        tabControl = new TabControl();\n        tabControl.Dock = DockStyle.Fill;\n\n        // Add some sample tabs\n        tabControl.TabPages.Add("Tab 1");\n        tabControl.TabPages.Add("Tab 2");\n        tabControl.TabPages.Add("Tab 3");\n\n        // Subscribe to the MouseDoubleClick event\n        tabControl.MouseDoubleClick += new MouseEventHandler(TabControl_MouseDoubleClick);\n\n        this.Controls.Add(tabControl);\n    }\n\n    private void TabControl_MouseDoubleClick(object sender, MouseEventArgs e)\n    {\n        TabControl tabControl = sender as TabControl;\n        for (int i = 0; i < tabControl.TabCount; i++)\n        {\n            if (tabControl.GetTabRect(i).Contains(e.Location))\n            {\n                MessageBox.Show($"Tab {i + 1} was double-clicked.");\n                break;\n            }\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new TabControlDoubleClick());\n    }\n}