using System;\nusing System.Runtime.InteropServices;\n\npublic class PDFSearch\n{\n    [DllImport("query.dll", SetLastError = true, CharSet = CharSet.Unicode)]\n    private extern static int LoadIFilter(\n        string pwcsPath,\n        [MarshalAs(UnmanagedType.IUnknown)] object pUnkOuter,\n        ref IFilter ppIUnk);\n\n    public void LoadFilter(string filePath)\n    {\n        IFilter filter = null;\n        int result = LoadIFilter(filePath, null, ref filter);\n\n        if (result != 0) // 0 indicates S_OK (success)\n        {\n            string errorMessage = new System.ComponentModel.Win32Exception(result).Message;\n            Console.WriteLine($"Error loading IFilter: {result} - {errorMessage}");\n            // Additional logging or error handling can be done here\n        }\n        else\n        {\n            Console.WriteLine("IFilter loaded successfully.");\n            // Proceed with using the filter\n        }\n    }\n}\n\npublic interface IFilter\n{\n    // Define IFilter methods here\n}