protected override void OnLoad(EventArgs e)\n{\n    base.OnLoad(e);\n\n    if (!IsAuthorized(param1, param2, ...))\n    {\n        Response.Write("Not authorized");\n        // Optionally, you can disable controls or redirect to a login page\n        DisableControls();\n        return;\n    }\n\n    // Other OnLoad logic\n}\n\nprotected void MyButton1_Click(object sender, EventArgs e)\n{\n    if (!IsAuthorized(param1, param2, ...))\n    {\n        Response.Write("Not authorized");\n        return;\n    }\n\n    // Button1 click logic\n}\n\nprotected void MyButton2_Click(object sender, EventArgs e)\n{\n    if (!IsAuthorized(param1, param2, ...))\n    {\n        Response.Write("Not authorized");\n        return;\n    }\n\n    // Button2 click logic\n}\n\nprivate void DisableControls()\n{\n    // Disable the controls if necessary, e.g.\n    MyButton1.Enabled = false;\n    MyButton2.Enabled = false;\n    // Other controls\n}